import React from 'react'
import './Product.css'
import { useStateValue } from '../StateProvider';

export default function Product({id, title, image, price,rating}) {
  const [{basket}, dispatch]= useStateValue()


  const addToBasket = () => {
    // dispatch the item into the data layer
    dispatch({
      type: "ADD_TO_BASKET",
      item: {
        id: id,
        title: title,
        image: image,
        price: price,
        rating: rating,
      },
    });
